#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Tue Jan  6 12:07:15 2026Data Class Game object@author: andrewgerber"""from dataclasses import dataclassfrom datetime import datefrom typing import Optional@dataclass(slots=True)class Game:    # --- Metadata ---    date: Optional[date] = None    team_name_abbr: str = ""    game_location: str  = "" # HOME / AWAY    opp_name_abbr: str  = ""    game_result: str    = ""   #"W, 20-13"    # --- Rushing ---    rush_att: int = 0    rush_yds: int = 0    rush_td: int = 0    rush_long: int = 0    rush_yds_per_att: float = 0.0    # --- Receiving ---    targets: int = 0    rec: int = 0    rec_yds: int = 0    rec_yds_per_rec: float = 0.0    rec_td: int = 0    rec_long: int = 0    catch_pct: float = 0.0    rec_yds_per_tgt: float = 0.0    # --- Totals ---    touches: int = 0    yds_per_touch: float = 0.0    yds_from_scrimmage: int = 0    rush_receive_td: int = 0    fumbles: int = 0    #   Helpers    @property    def is_home(self) -> bool:        return self.game_location.upper() == "HOME"    @property    def total_tds(self) -> int:        return self.rush_td + self.rec_td    @property    def has_fumble(self) -> bool:        return self.fumbles > 0