"""Created on Mon Jan  5 15:11:49 2026Player Scraper that uses BeautifulSoup to parse the html and return Player Object@author: andrewgerber"""#   Define Importsimport refrom utils import doesExistfrom gameLogScraper import getGameLogFromSoupimport PlayerInfo as PIimport Player as P#-- Imports#   Get the Player data and Return the player object from html_soupdef getPlayerDataFromSoup(html_soup):        if not doesExist(html_soup):        print("No HTML was returned by the response.")        return None        #   Get the Player Info    player_info = {"PlayerInfo": getPlayerInfo(html_soup)}    player_game_log = {"Game Log": getGameLogFromSoup(html_soup)}    return player_info, player_game_log#   Get the Player info from soupdef getPlayerInfo(html_soup):    try:        # Find the meta div        meta = html_soup.find("div", id="meta")        if not meta:            print("No meta tag found for player info.")            return {}                name_tag = meta.find("h1")        player_name = name_tag.text.strip() if name_tag else None        height = None        weight = None        position = None        team = None                for p in meta.find_all("p"):            text = p.get_text(" ", strip=True)                # Height & Weight            if "lb" in text and "cm" in text:                height_match = re.search(r"(\d+-\d+)", text)                weight_match = re.search(r"(\d+)\s*lb", text)                    if height_match:                    height = height_match.group(1)                if weight_match:                    weight = weight_match.group(1)                # Position            if text.startswith("Position"):                position = text.replace("Position:", "").replace("Position :", "").split("â–ª")[0].strip()                # Team            a = p.find("a")            if a and a.get("href", "").startswith("/teams/"):                team = a.text.strip()            player_info = PI.PlayerInfo(name = player_name, position = position, team = team, height = height, weight = weight)        player_info.displaySelf()        player = P.Player()        print(player.PlayerInfo)        return player_info                return     except:        print("An error occured gathering player info")        return None